mapper.select:
select asa.id, asa.user_id, asa.app_business_name, asa.app_business_address, asa.app_business_mobile, asa.app_business_contacts, asa.max_call_number, asa.creator_id, asa.apply_status, asa.create_time, asa.last_update, mt.name as major_type_name, mt.id as major_type_id from admin_service_apply asa left join admin_major_type_service_apply_mapping amt on asa.id = amt.service_apply_id left join major_type mt on mt.id = amt.major_type_id where `status` = 'active'
and apply_status = #{applyStatus}
and asa.app_business_name like concat(concat('%',#{appBusinessName}),'%')
order by create_time desc limit #{startNum}, #{perPage}

select count(*) from admin_service_apply asa left join admin_major_type_service_apply_mapping amt on asa.id = amt.service_apply_id left join major_type mt on mt.id = amt.major_type_id where `status` = 'active'
and apply_status = #{applyStatus}
and asa.app_business_name like concat(concat('%',#{appBusinessName}),'%')

select asa.id, asa.user_id, asa.app_business_name, asa.app_business_address, asa.app_business_mobile, asa.app_business_contacts, asa.max_call_number, asa.creator_id, asa.apply_status, asa.create_time, asa.last_update, mt.name as major_type_name, mt.id as major_type_id from admin_service_apply asa left join admin_major_type_service_apply_mapping amt on asa.id = amt.service_apply_id left join major_type mt on mt.id = amt.major_type_id where `status` = 'active'
and apply_status = #{applyStatus}
and asa.app_business_name like concat(concat('%',#{appBusinessName}),'%')
and asa.id = #{id}
