mapper.select:
select asa.id, asa.user_id, asa.creator_id, asa.apply_status, asa.create_time, asa.last_update, mt.name as major_type_name, mt.id as major_type_id, au.name as creatorName, asa.username, asa.email, asa.reject_reason, asa.project_id, asa.project_name, asa.taskCount, asa.callCount, asa.call_number as project_call_number from (select a.id, a.user_id, a.creator_id, a.apply_status, a.create_time, a.last_update, a.username, a.email, a.reject_reason, a.`status`, b.id as project_id, b.name as project_name, b.call_number, (select count(*) from task where project_id = a.project_id) as taskCount, (select sum(calling_times) from api_calling_count where project_id=a.project_id) as callCount from admin_service_apply a, project b where a.`status` = 'active' and b.`status` = 'active' and a.project_id = b.id
and a.apply_status = #{applyStatus}
and b.id like concat(concat('%',#{projectId}),'%')
and b.name like concat(concat('%',#{name}),'%')
and b.`type` = #{projectType}
order by a.create_time desc limit #{startNum}, #{perPage} )asa,admin_major_type_service_apply_mapping am,major_type mt,admin_user au where asa.`status` = 'active' and asa.id=am.service_apply_id and am.`status`='active' and mt.id=am.major_type_id and mt.`status`='active' and au.id=asa.creator_id and au.`status`='active'

select count(*) from admin_service_apply a, project b where a.`status` = 'active' and b.`status` = 'active' and a.project_id = b.id
and a.apply_status = #{applyStatus}
and b.id like concat(concat('%',#{projectId}),'%')
and b.name like concat(concat('%',#{name}),'%')
and b.`type` = #{projectType}
