mapper.select:
		select id, id as _id,  `name`, task_status, task_status as status, user_id, user_id as author,
		 create_time,create_time as createdAt, stitch_image_path, result, rows, major_type,last_update as updatedAt, last_update as lastUpdateTime
		from task
		where `status` = 'active'

			and user_id = #{userId}


			and id = #{id}


			and `name` like concat(concat('%',#{name}),'%')


			and `major_type` = #{majorType}


			and `task_status` = #{taskStatus}


    		and last_update  >= #{startTime} 


    		and last_update  <= #{endTime} 

		order by last_update desc
		limit #{startNum}, #{perPage}

		select id, `name`, task_status, user_id, create_time,
		stitch_image_path, result, rows, major_type, goods_info
		from task
		where `status` = 'active'

			and user_id = #{userId}


			and id = #{id}


			and `name` like concat(concat('%',#{name}),'%')


			and `task_status` = #{taskStatus}


			and `major_type` = #{majorType}


    		and last_update  >= #{startTime} 


    		and last_update  <= #{endTime} 

		order by last_update desc

		select count(*)
		from task
		where `status` = 'active'

			and user_id = #{userId}


			and id = #{id}


			and `name` like concat(concat('%',#{name}),'%')


			and `major_type` = #{majorType}


			and `task_status` = #{taskStatus}


    		and last_update  >= #{startTime} 


    		and last_update  <= #{endTime}

select id, `name`, task_status, user_id, create_time, stitch_image_path, result, rows, major_type,goods_info,last_update,identify_success_times from task where `status` = 'active'
and user_id = #{userId}
and last_update < (select last_update from task where id=#{taskId}) and id!=#{taskId} order by last_update desc limit 1
