mapper.select:
		SELECT task.*, m.description as majorTypeName from
			(select t.id, t.id as _id,  t.`name`, t.task_status, t.task_status as status, t.user_id, t.user_id as author,
				t.create_time, t.create_time as createdAt, t.stitch_image_path, t.result, t.rows, t.major_type,
		 		t.last_update as updatedAt, t.last_update as lastUpdateTime, t.identify_success_times
				from task t
				where t.`status` = 'active'

					and t.user_id = #{userId}


					and t.id = #{id}


					and t.`name` like concat(concat('%',#{name}),'%')


					and t.`major_type` = #{majorType}


					and t.`task_status` = #{taskStatus}


    				and t.last_update  >= #{startTime} 


    				and t.last_update  <= #{endTime} 


    				and t.project_id  = #{projectId}

				order by t.last_update desc
				limit #{startNum}, #{perPage}
			) task 
			left join major_type m on m.name=task.major_type

		select id, `name`, task_status, user_id, create_time,
		stitch_image_path, result, rows, major_type, goods_info,identify_success_times
		from task
		where `status` = 'active'

			and user_id = #{userId}


			and id = #{id}


			and `name` like concat(concat('%',#{name}),'%')


			and `task_status` = #{taskStatus}


			and `major_type` = #{majorType}


    		and last_update  >= #{startTime} 


    		and last_update  <= #{endTime} 

		order by last_update desc

		select count(*)
		from task use index (idx_last_update) 
		where `status` = 'active'

			and user_id = #{userId}


			and id = #{id}


			and `name` like concat(concat('%',#{name}),'%')


			and `major_type` = #{majorType}


			and `task_status` = #{taskStatus}


    		and last_update  >= #{startTime} 


    		and last_update  <= #{endTime} 


    		and project_id  = #{projectId}

select id, `name`, task_status, user_id, create_time, stitch_image_path, result, rows, major_type, goods_info, last_update, identify_success_times from task where `status` = 'active'
and user_id = #{userId}
and last_update < (select last_update from task where id=#{taskId}) and id != #{taskId} order by last_update desc limit 1

		select id, id as _id,  `name`, task_status, task_status as status, user_id, user_id as author,
		 create_time,create_time as createdAt, stitch_image_path, result, rows, major_type,last_update as updatedAt,
		 last_update as lastUpdateTime,identify_success_times
		from task
		where `status` = 'active'

			and user_id = #{userId}


			and id = #{id}


			and `name` like concat(concat('%',#{name}),'%')


			and `major_type` in

  				#{item}  



			and `task_status` = #{taskStatus}


    		and last_update  >= #{startTime} 


    		and last_update  <= #{endTime} 

		order by last_update desc
		limit #{startNum}, #{perPage}

		select count(*)
		from task
		where `status` = 'active'

			and user_id = #{userId}


			and id = #{id}


			and `name` like concat(concat('%',#{name}),'%')


			and `major_type` in

  				#{item}  



			and `task_status` = #{taskStatus}


    		and last_update  >= #{startTime} 


    		and last_update  <= #{endTime}
